require 'sinatra'

#localhost:4567
get '/' do #'/' 는 root를 의미
    send_file 'index.html'
end

#localhost:4567/welcome
get '/welcom' do
    send_file 'welcom.html'
end

get '/squre/:num' do
    @num = params[:num]
    erb :hello
end

get '/welcom/:name' do
    @name = params[:name] # @ 다른 파일에 값을 넘겨 줄 수 있음
    erb :name
end

get '/lotto' do
    @lotto = (1..45).to_a.sample(6)
    erb :lotto
end

get '/google' do
    erb :google
end

get '/search' do
    @q = params[:q]
    @name = params[:name]
    erb :search
end

get '/lunch' do
    @lunch = ["멀캠 20층","순남시래기","명동칼국수","강남목장","시골집"].sample
    @img_url = {
        "멀캠 20층" => "https://lh3.googleusercontent.com/-ty8d4LjoRko/TrihLJ02z1I/AAAAAAAACoo/FC2KzUhkp4w/w530-h707-n/11.%2B11.%2B7.%2B-%2B1",
        "순남시래기" => "data:image/jpeg;base64,",
        "명동칼국수" =>"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT4KVIod3pxFehhX1mwQ5tOeBaCZR7BB2C5GRSIerIUVnihcIQ"
    }
    erb :lunch
end